CREATE TABLE IF NOT EXISTS `product` (`id` INTEGER NOT NULL AUTO_INCREMENT PRIMARY KEY, `product_code` VARCHAR(100) NULL DEFAULT NULL, `product_name` VARCHAR(100) NULL DEFAULT NULL, `product_brand` VARCHAR(100) NULL DEFAULT NULL, `product_status` VARCHAR(45) NULL DEFAULT NULL, `product_date` VARCHAR(45) NULL DEFAULT NULL, `product_quantify` VARCHAR(100) NULL DEFAULT NULL, `product_price` VARCHAR(100) NULL DEFAULT NULL);